import React from "react";
import axios from "axios";

export default class Game extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      deckID: "",
      cards: [],
    };
  }

  componentDidMount() {
    axios
      .get("https://deckofcardsapi.com/api/deck/new/shuffle/?deck_count=1")
      .then((res) => {
        console.log(res.data);
        this.setState({ deckID: res.data.deck_id });
      });
  }

  drawCard() {
    axios
      .get(
        `https://deckofcardsapi.com/api/deck/${this.state.deckID}/draw/?count=1`
      )
      .then((res) => {
        console.log(res.data);
        this.state.cards.push(res.data);
        // const newCards = newCards.push();
        // this.setState({ cards: newCards });
      });
  }

  render() {
    return (
      <div>
        <button onClick={this.drawCard.bind(this)}>Draw a card!</button>
        {this.state.cards.map((card) => card)}
      </div>
    );
  }
}
