import React from "react";

import Card from "./Card";

export default class Hand extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      cards: [],
    };
  }

  onDrop = (e) => {
    e.preventDefault();
    const state = e.dataTransfer.getData("cardState");
    this.state.cards.push(<Card code={state.code} data={state} />);
    console.log(this.state.cards);
  };

  onDragOver = (e) => {
    e.preventDefault();
  };

  render() {
    return (
      <div
        className="row"
        style={{ borderStyle: "solid", height: 100, backgroundColor: "red" }}
        onDrop={this.onDrop}
        onDragOver={this.onDragOver}
      >
        {this.state.cards}
      </div>
    );
  }
}
