import React from "react";

export default class Hand extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      cards: [],
    };
  }

  onDrop = (e) => {
    e.preventDefault();
    const card = e.dataTransfer.getData("cardId");
    this.state.cards.push(card);
  };

  render() {
    return (
      <div
        class="row"
        style={{ borderStyle: "solid", height: 100, backgroundColor: "red" }}
        onDrop={this.onDrop}
      >
        {this.state.cards}
      </div>
    );
  }
}
