import React from "react";
import axios from "axios";

export default class Game extends React.Component {
  state = {
    gameID: "",
  };

  componentDidMount() {
    axios
      .get("https://deckofcardsapi.com/api/deck/new/shuffle/?deck_count=1")
      .then((res) => {
        console.log(res.data);
        this.setState({ gameID: res.data.deck_id });
      });
  }

  drawCard() {
    axios
      .get(
        `https://deckofcardsapi.com/api/deck/${this.state.gameID}/draw/?count=1`
      )
      .then((res) => {
        console.log(res.data);
      });
  }

  render() {
    console.log(this.state);
    return <button onClick={this.drawCard}>Draw a card!</button>;
  }
}
