import React from "react";

export default class Game extends React.Component {
  constructor(props) {
    this.props = {
      image: props.data.image,
      suit: props.data.suit,
      value: props.data.value,
      code: props.data.code,
    };
  }

  onDragStart = (e) => {
    e.dataTransfer.setData("cardDetails", JSON.stringify(this.props));
  };

  onDragOver = (e) => {
    e.stopPropagation();
  };

  onDragEnd = (e) => {
    const x = e.dataTransfer.getData("cardDetails");
    console.log(x);

    const target = e.target;
    setTimeout(() => {
      target.style.display = "none";
    }, 0);
  };

  render() {
    const text = `${this.props.suit} of ${this.props.value}`;
    return (
      <div
        draggable={true}
        onDragStart={this.onDragStart}
        onDragOver={this.onDragOver}
        onDragEnd={this.onDragEnd}
      >
        <img alt={text} src={this.props.image} width="75" />
      </div>
    );
  }
}
